    knowledge_focus = ARRAY['Synaptic Plasticity Mechanisms', 'Zwei-Prozess-Schlafmodell', '4-4-6-2 Atemprotokoll', 'Self-Determination Theory', 'MBCT 8-Wochen-Protokoll', 'HRV-Biofeedback-Training'],
