-- Neue Coaches hinzufügen: Dr. Vita Femina und Markus Rühl

INSERT INTO public.coach_specializations (coach_id, name, specialization_description, core_philosophy, expertise_areas, knowledge_focus, methodology, created_at, updated_at) VALUES
('dr_vita_femina', 'Dr. Vita Femina - Die Frauengesundheits-Spezialistin', 'Die führende Expertin für hormonelle Gesundheit und zyklusbasierte Optimierung bei Frauen. Spezialisiert auf Menstruationszyklus-Management, Schwangerschafts-Fitness, Menopause-Navigation und weibliche Sportmedizin mit evidenzbasiertem Ansatz für jede Lebensphase.', 'Dein weiblicher Körper ist ein komplexes hormonelles Meisterwerk. Jede Phase deines Zyklus bietet einzigartige Optimierungsmöglichkeiten. Wir arbeiten MIT deinen Hormonen, nicht gegen sie, für nachhaltige Gesundheit und Performance.', ARRAY['Menstruationszyklus-Optimierung', 'Schwangerschafts- und Postpartum-Training', 'Menopause-Management', 'Weibliche Sportmedizin', 'Hormonelle Balance', 'Reproduktive Gesundheit'], ARRAY['Zyklusbasierte Periodisierung', 'Östrogen-Progesteron Balance', 'HRT-Strategien', 'RED-S Prävention', 'Eisenmanagement bei Athletinnen', 'Beckenboden-Rehabilitation'], 'Evidenzbasierte gynäkologische Medizin kombiniert mit sports science für frauen-spezifische Gesundheitsoptimierung. Hormonell-guidierte Trainings- und Ernährungsstrategien für alle Lebensphasen.', now(), now()),

('markus_ruehl', 'Markus Rühl - Der Hardcore Bodybuilding-Veteran', 'Der legendäre deutsche Bodybuilding-Profi mit über 30 Jahren Wettkampf- und Trainingserfahrung. Spezialisiert auf extremen Muskelaufbau, Contest-Prep, alte Schule Bodybuilding-Methoden und mental-toughness Development für serious lifters.', 'Bodybuilding ist kein Hobby - es ist eine Lebensphilosophie. Jeder Satz zählt, jede Wiederholung formt Charakter. Old School Intensität trifft auf moderne Wissenschaft. Keine Ausreden, nur Resultate.', ARRAY['Extremer Muskelaufbau', 'Contest Preparation', 'Old School Bodybuilding', 'Mental Toughness Training', 'Volumen-Intensitäts-Training', 'Supplement-Strategien'], ARRAY['German Volume Training', 'FST-7 Methoden', 'Contest-Prep Periodisierung', 'Carb-Cycling Strategien', 'Intensity Techniques', 'Mind-Muscle Connection'], 'Hardcore Bodybuilding-Methoden basierend auf jahrzehntelanger Wettkampferfahrung. Extreme Intensität, perfekte Ausführung und mentale Stärke für maximalen Muskelaufbau und Contest-Performance.', now(), now());